CREATE TABLE public.inspection_templates (
    id uuid DEFAULT gen_random_uuid() NOT NULL,
    type text,
    structure jsonb NOT NULL,
    created_at timestamp without time zone DEFAULT now(),
    updated_at timestamp without time zone DEFAULT now(),
    inspection text,
    CONSTRAINT inspection_templates_type_check CHECK ((type = ANY (ARRAY['horse'::text, 'combo'::text, 'trailer'::text])))
);

ALTER TABLE public.inspection_templates OWNER TO postgres;

CREATE TABLE public.inspections (
    id bigint NOT NULL,
    vehicle_id bigint NOT NULL,
    driver_id bigint,
    odo_reading integer NOT NULL,
    inspection_date timestamp with time zone DEFAULT now(),
    checklist jsonb NOT NULL,
    overall_status text,
    category text,
    remarks text,
    created_at timestamp with time zone DEFAULT now(),
    trip_id bigint,
    CONSTRAINT inspections_category_check CHECK ((category = ANY (ARRAY['A'::text, 'B'::text]))),
    CONSTRAINT inspections_overall_status_check CHECK ((overall_status = ANY (ARRAY['OK'::text, 'Faulty'::text])))
);

ALTER TABLE public.inspections OWNER TO postgres;

ALTER TABLE public.inspections ALTER COLUMN id ADD GENERATED ALWAYS AS IDENTITY (
    SEQUENCE NAME public.inspections_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

CREATE TABLE public.load_inspections (
    id bigint NOT NULL,
    driver_id uuid NOT NULL,
    trip_id bigint,
    images text[] NOT NULL,
    inspection_date timestamp without time zone DEFAULT now() NOT NULL,
    status text DEFAULT 'pending'::text NOT NULL,
    created_at timestamp without time zone DEFAULT now(),
    updated_at timestamp without time zone DEFAULT now()
);

ALTER TABLE public.load_inspections OWNER TO postgres;

ALTER TABLE public.load_inspections ALTER COLUMN id ADD GENERATED ALWAYS AS IDENTITY (
    SEQUENCE NAME public.load_inspections_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

CREATE TABLE public.trailer_inspection (
    driver_id integer,
    vehicle_id integer,
    inspected boolean DEFAULT false NOT NULL,
    inspection_date timestamp with time zone DEFAULT now(),
    created_at timestamp with time zone DEFAULT now(),
    updated_at timestamp with time zone DEFAULT now(),
    user_id uuid,
    id bigint NOT NULL,
    type text,
    trailer text
);

ALTER TABLE public.trailer_inspection OWNER TO postgres;

ALTER TABLE public.trailer_inspection ALTER COLUMN id ADD GENERATED ALWAYS AS IDENTITY (
    SEQUENCE NAME public.trailer_inspection_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

CREATE TABLE public.vehicle_inspections (
    driver_id integer,
    vehicle_id integer,
    inspected boolean DEFAULT false NOT NULL,
    inspection_date timestamp with time zone DEFAULT now(),
    created_at timestamp with time zone DEFAULT now(),
    updated_at timestamp with time zone DEFAULT now(),
    user_id uuid,
    id bigint NOT NULL,
    type text,
    trailer text,
    second_trailer text
